import { Link } from "react-router-dom";
import TeacherSidebar from "./teachersidebar";
import React from 'react';
import {useState, useEffect} from 'react';
import axios from "axios";
import Swal from 'sweetalert2';

const baseUrl = 'http://127.0.0.1:8000/apiview';

function TeacherCourses() {
    const [CourseData, setCourseData] = useState([]);
    // const [teacherData, setteacherData] = useState([]);
    const teacherId = localStorage.getItem('teacherId')

    useEffect(() => {
        try{
            axios.get(baseUrl + '/teacher-course/'+teacherId)
            .then((res) => {
                setCourseData(res.data)
            });
        }catch(error){
            console.log(error);
        }
    },[]);

    const handleDeleteChange = (course_id) => {
        Swal.fire({
            title: 'Confirm',
            text: 'Do you want to delete this course?',
            icon: 'info',
            confirmButtonText: 'Drop',
            showCancelButton: true,
        }).then((result) => {
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/course-details/' + course_id)
                    .then((res) => {
                        Swal.fire('success', 'Course has been removed.');
                        try{
                            axios.get(baseUrl + '/teacher-course/' + teacherId)
                            .then((res)=>{
                                setCourseData(res.data);
                            })
                        }catch(error){
                            console.log(error);
                        }
                    })
                }catch(error){
                    console.log(error);
                }
            }else{
                Swal.fire('error', 'Course not removed..!');
            }
        })
    }

    console.log(CourseData);

    return(
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <TeacherSidebar />
                </aside>
                <section className="col-md-9">
                    <div className="card">
                        <h5 className="card-header">My Courses</h5>
                        <div className="card-body">
                            <table className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Images</th>
                                        <th>Created By</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {/* {CourseData.map((course, index) =>  */}
                                        <tr>
                                            <td><Link to={'/all-chapter/'}>'{course.title}'</Link></td>
                                            <td><img width="80px" src='{}' className="rounded" alt='{course.title}' /></td>
                                            <td><Link to={'teacher-details/'}>''</Link></td>
                                            <td>
                                                <Link to={'/edit-course/'} className="btn btn-info btn-sm active">Edit</Link>
                                                <Link to={'/add-chapter/'} className="btn btn-success btn-sm active ms-2">Add Chapter</Link>
                                                <button onClick={()=>handleDeleteChange()} className="btn btn-danger active btn-sm ms-2">Drop</button>
                                            </td>
                                        </tr>
                                    {/* )}  */}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default TeacherCourses;
