import { Link, useParams } from "react-router-dom";
import Sidebar from "./StudentSidebar";
import React, { useState, useEffect } from 'react';
import axios from "axios";

const baseUrl = 'http://127.0.0.1:8000/apiview';

function TakeQuizes() {
    const teacherId = localStorage.getItem('teacherId')
    const [QuestionData, setQuestionData] = useState([]);
    const {quiz_id} = useParams();

    useEffect(() => {
        try{
            axios.get(baseUrl + '/teacher-quiz-question/'+quiz_id+'/'+'1')
            .then((res) => {
                setQuestionData(res.data);
            });
        }catch(error){
            console.log(error);
        }
    },[]);
    return(
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <Sidebar />
                </aside>
                <section className="col-md-9">
                <h5 className="mb-3 border-bottom pb-1">Quiz Title</h5>
                    {QuestionData.map((question, index)=>
                        <div className="card">
                            <h5 className="card-header">{question.question_title}</h5>
                            <div className="card-body">
                                <table className="table table-bordered">
                                    <tbody>

                                            <tr>
                                                <td><button className="btn btn-outline-secondary">{question.option1}</button></td>
                                            </tr>
                                            <tr>
                                                <td><button className="btn btn-outline-secondary">{question.option2}</button></td>
                                            </tr>
                                            <tr>
                                                <td><button className="btn btn-outline-secondary">{question.option3}</button></td>
                                            </tr>
                                            <tr>
                                                <td><button className="btn btn-outline-secondary">{question.option4}</button></td>
                                            </tr>

                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}
                </section>
            </div>
        </div>
    );
}

export default TakeQuizes;
