import { Link } from "react-router-dom";
import MyCourses from './StudentCourses'
import StudentSidebar from "./StudentSidebar";
import React from 'react';
import {useState, useEffect} from 'react';
import axios from "axios";
import Swal from 'sweetalert2';

const baseUrl = 'http://127.0.0.1:8000/apiview';


function StudentProfileSettings(){
    const studentId = localStorage.getItem('teacherId')
    const [TeacherData, setTeacherData] = useState({
        full_name: '',
        bio: '',
        email: '',
        qualification: '',
        p_img: '',
        skills: '',
    });

    useEffect(() => {
        // Fetch currect course data
        try{
            axios.get(baseUrl + '/teacher/' + teacherId)
            .then((res) => {
                setTeacherData({
                    full_name: res.data.full_name,
                    bio: res.data.bio,
                    email: res.data.email,
                    qualification: res.data.qualification,
                    profile_img: res.data.profile_img,
                    p_img: '',
                    skills: res.data.skills,
                })
            });
        }catch(error){
            console.log(error);
        }
        //End fetch course data
    },[]);
    return (
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <StudentSidebar />
                </aside>
                <section className="col-md-9">
                    <form>
                        <div className="mb-3">
                            <label for="exampleInputEmail1" className="form-label">Name</label>
                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputEmail1" className="form-label">Email address</label>
                            <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputPassword1" className="form-label">Username</label>
                            <input type="file" className="form-control" id="exampleInputPassword1" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputPassword1" className="form-label">Interested Category</label>
                            <input type="text" className="form-control" id="exampleInputPassword1" />
                        </div>
                        <hr></hr>
                        <button type="submit" className="btn btn-primary">Update</button>
                    </form>
                </section>
            </div>
        </div>
    );
}

export default StudentProfileSettings;
