// import { Link } from "react-router-dom";
// import MyStudents from './mystudents';
import TeacherSidebar from "./TeacherSidebar";
import React from 'react';
import {useState, useEffect} from 'react';
import axios from "axios";
import {useParams} from 'react-router-dom';
import Swal from 'sweetalert2';

const baseUrl = 'http://127.0.0.1:8000/apiview';

function TeacherProfileSettings(){
    const [courseData, setcourseData] = useState({
        category: '',
        title: '',
        description: '',
        f_img: '',
        techs: '',
    });

    useEffect(() => {
        // Fetch currect course data
        try{
            axios.get(baseUrl + '/course/' + course_id)
            .then((res) => {
                setcourseData({
                    category: res.data.category,
                    title: res.data.title,
                    description: res.data.description,
                    prev_img: res.data.featured_img,
                    f_img: '',
                    techs: res.data.techs,
                })
            });
        }catch(error){
            console.log(error);
        }
        //End fetch course data
    },[]);

    const handleChange = (event) => {
        setcourseData({
            ...courseData,
            [event.target.name]:event.target.value
        })
    };

    const handleFileChange = (event) => {
        setcourseData({
            ...courseData,
            [event.target.name]:event.target.files[0]
        })
    };

    return (
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <TeacherSidebar />
                </aside>
                <section className="col-md-9">
                    <form>
                        <div className="mb-3">
                            <label for="exampleInputEmail1" className="form-label">Name</label>
                            <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputEmail1" className="form-label">Email address</label>
                            <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputPassword1" className="form-label">Password</label>
                            <input type="file" className="form-control" id="exampleInputPassword1" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputPassword1" className="form-label">Password</label>
                            <input type="password" className="form-control" id="exampleInputPassword1" />
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputPassword1" className="form-label">Skills</label>
                            <input type="text" className="form-control" id="exampleInputPassword1" />
                        </div>
                        <hr></hr>
                        <button type="submit" className="btn btn-primary">Update</button>
                    </form>
                </section>
            </div>
        </div>
    );
}

export default TeacherProfileSettings;
