import { Link } from "react-router-dom";
import TeacherSidebar from "./teachersidebar";
import React from 'react';
import {useState, useEffect} from 'react';
import axios from "axios";


const baseUrl = 'http://127.0.0.1:8000/apiview';

function AddCourses() {
    const [Cates, setCates] = useState([]);
    const [courseData, setcourseData] = useState({
        category: '',
        title: '',
        description: '',
        f_img: '',
        techs: ''
    });

    useEffect(() => {
        try{
            axios.get(baseUrl + '/category')
            .then((res) => {
                setCates(res.data)
            });
        }catch(error){
            console.log(error);
        }
    },[]);

    const handleChange = ((event) => {
        setcourseData({
            ...courseData,
            [event.target.name]:event.target.value
        })
    });

    const handleFileChange = ((event) => {
        setcourseData({
            ...courseData,
            [event.target.name]:event.target.value
        })
    });

    const submitForm = ((e) => {
        const _formData = new FormData();
        _formData.append('category', courseData.category)
        _formData.append('teacher', courseData.category)
        _formData.append('category', courseData.category)
        _formData.append('category', courseData.category)
        _formData.append('category', courseData.category)
    })

    // console.log(Cates);

    return(
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <TeacherSidebar />
                </aside>
                <div className="col-9">
                    <div className="card">
                        <h5 className="card-header">Add Course</h5>
                        <div className="card-body">
                            <form>
                                <div className="mb-3">
                                    <label for="title" className="form-label">Category</label>
                                    <select name='category' className="form-control">
                                        {Cates.map((category, index) => {return <option key={index}>{category.title}</option>})}

                                    </select>
                                </div>
                                <div className="mb-3">
                                    <label for="description" className="form-label" htmlFor="email">Description</label>
                                    <textarea id="description" className="form-control"></textarea>
                                </div>
                                <div className="mb-3">
                                    <label for="video" className="form-label">Featured Image</label>
                                    <input type="file" id="video" className="form-control"/>
                                </div>
                                <div className="mb-3">
                                    <label for="techs" class="form-label">Technologies</label>
                                    <textarea id="techs" placeholder="Python, Django, JavaScripts, PHP, HTML, CSS..." className="form-control"></textarea>
                                </div>
                                <button type="submit" className="btn btn-primary">Add</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AddCourses;
