import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";
import CheckCourseInQuiz from "./CheckQuizInCourse";
import React from 'react';
import {useState, useEffect} from 'react';
import axios from "axios";
import Swal from 'sweetalert2';

const baseUrl = 'http://127.0.0.1:8000/apiview';

function TeacherCourses() {
    const [CourseData, setCourseData] = useState([]);
    const [TeacherCourseData, setTeacherCourseData] = useState([]);
    const [QuizData, setQuizData] = useState([]);
    const teacherId = localStorage.getItem('teacherId')
    const {course_id} = useParams();

    useEffect(() => {
        try{
            axios.get(baseUrl + '/teacher-course/'+teacherId)
            .then((res) => {
                setTeacherCourseData(res.data)
            });
        }catch(error){
            console.log(error);
        }

        try{
            axios.get(baseUrl + '/teacher-quiz/'+teacherId)
            .then((res) => {
                setQuizData(res.data);
            });
        }catch(error){
            console.log(error);
        }

        try{
            axios.get(baseUrl+'/quiz-assign-status/'+quiz_id+'/'+course_id)
            .then((res) => {
                setCourseData(res.data);
            });
        }catch(error){
            console.log(error);
        }

    },[]);

    const handleDeleteChange = (course_id) => {
        Swal.fire({
            title: 'Confirm',
            text: 'Do you want to delete this course?',
            icon: 'info',
            confirmButtonText: 'Drop',
            showCancelButton: true,
        }).then((result) => {
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/course/' + course_id)
                    .then((res) => {
                        Swal.fire('success', 'Course has been removed.');
                        try{
                            axios.get(baseUrl + '/teacher-course/' + teacherId)
                            .then((res)=>{
                                setTeacherCourseData(res.data);
                            })
                        }catch(error){
                            console.log(error);
                        }
                    })
                }catch(error){
                    console.log(error);
                }
            }else{
                Swal.fire('error', 'Course not removed..!');
            }
        })
    }

    const AssignToQuiz = (quiz_id)=>{
        // e.preventDefault();
        const _formData = new FormData();
        _formData.append('quiz', props.quiz);
        _formData.append('teacher', teacherId);
        _formData.append('course', props.quiz);

        try{
            axios.post(baseUrl + '/assign-quiz/', _formData,{
                headers: {
                    'content-type': 'multipart/form-data'
                }
            })
            .then((res) => {
                if(res.status==200||res.status==201){
                    window.location.reload();
                }
            });
        }catch(error){
            console.log(error);
        }
    };

    return(
        <div className="container mt-4">
            <div className="row">
                <aside className="col-md-3">
                    <TeacherSidebar />
                </aside>
                <section className="col-md-9">
                    <div className="card">
                        <h5 className="card-header">My Courses</h5>
                        <div className="card-body">
                            <table className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Images</th>
                                        <th>Enrolled</th>
                                        <th>Action</th>
                                        <th>Rating</th>
                                        <th>Assign Quiz</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {TeacherCourseData.map((course, index) => 
                                       <><tr>

                                            <td><Link to={'/all-chapter/'+course.id}>{course.title}</Link></td>
                                            <td><img width="80px" src={course.featured_img} className="rounded" alt={course.title} /></td>
                                            <td><Link to={`/enrolled-student/`+ course.id}>{course.total_enrolled}</Link></td>
                                            <td>
                                                <Link to={'/edit-course/'+course.id} className="btn btn-info btn-sm active">Edit</Link>
                                                <Link to={'/add-chapter/'+course.id} className="btn btn-success btn-sm active ms-2">Add Chapter</Link>
                                                <button onClick={()=>handleDeleteChange(+course.id)} className="btn btn-danger active btn-sm ms-2">Drop</button>
                                            </td>
                                            {course.course_rating &&
                                                <td><p>{course.course_rating}/5</p></td>
                                            }
                                            {!course.course_rating &&
                                                <td><p>Not yet rated</p></td>
                                            }
                                            <td>
                                            <select name='quiz' id='quiz' value={QuizData.id} checkcourseinquiz className="form-control">
                                                <option selected disabled>Select</option>
                                                {QuizData.map((quiz, index) => {return <option key={index} value={quiz.id}>{quiz.title}</option>})}
                                            </select>
                                                {/* <button onClick={()=>AssignToQuiz(QuizData.id)} type="submit" className="btn btn-primary">Add</button> */}
                                            </td>
                                        </tr>
                                        </>
                                        )}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
}

export default TeacherCourses;
