import { Link } from "react-router-dom";
import React from 'react'


function Registration() {
    const [teacherData, setteacherData] = useState({
        "full_name": "",
        "email": "",
        "password": "",
        "qualification": "",
        "phone_no": "",
        "skills": "",
        "status": "",
    });

    // change element value
    const handleChange = (event) => {
        // console.log(event.target.name, event.target.value)
        setteacherData({
            ...teacherData,
            [event.target.name]:event.target.value
        });
    }
    // end

    // submit form

    const handleForm=(e)=>{
        e.preventDefault();
        const teacherFormData = new FormData();
        teacherFormData.append("full_name", teacherData.full_name)
        teacherFormData.append("email", teacherData.email)
        teacherFormData.append("password", teacherData.password)
        teacherFormData.append("qualification", teacherData.qualification)
        teacherFormData.append("phone_no", teacherData.phone_no)
        teacherFormData.append("skills", teacherData.skills)

        try{
            axios.post(baseUrl, teacherFormData).then((res)=>{
                setteacherData({
                    "full_name": "",
                    "email": "",
                    "password": "",
                    "qualification": "",
                    "phone_no": "",
                    "skills": "",
                    "status": "success",
                });
            });
        }catch(err){
            console.log('error',err)
            setteacherData({'Status': 'error'})
        }
    };
    // End
    return(
        <div className="container mt-4">
            <div className="col-6 offset-3">
                <div className="card">
                    <h5 className="card-header">User Registration</h5>
                    <div className="card-body">
                        <form>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Name</label>
                                <input type="text" className="form-control"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Email</label>
                                <input type="email" className="form-control"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputPassword1" className="form-label">Password</label>
                                <input type="password" className="form-control" id="exampleInputPassword1"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Interest Field</label>
                                <textarea className="form-control"></textarea>
                                <div id="emailHelp" className="form-text">Ex: Python, Django, JavaScript</div>
                            </div>
                            <button type="submit" className="btn btn-primary">Register</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Registration;
