// import { Link } from "react-router-dom";
import React from 'react'
import { useEffect, useState } from "react";
import axios from 'axios';

const baseUrl = "http://127.0.0.1:8000/apiview/teacher/";


function Registration() {
    const [StudentData, setStudentData] = useState({
        "full_name": "",
        "email": "",
        "password": "",
        "username": "",
        "interest": "",
        "status": "",
    });

    // change element value
    const handleChange = (event) => {
        // console.log(event.target.name, event.target.value)
        setStudentData({
            ...StudentData,
            [event.target.name]:event.target.value
        });
    }
    // end

    // submit form

    const handleForm=(e)=>{
        e.preventDefault();
        const studentFormData = new FormData();
        studentFormData.append("full_name", StudentData.full_name)
        studentFormData.append("email", StudentData.email)
        studentFormData.append("password", StudentData.password)
        studentFormData.append("qualification", StudentData.qualification)
        studentFormData.append("phone_no", StudentData.phone_no)
        studentFormData.append("skills", teacherData.skills)

        try{
            axios.post(baseUrl, teacherFormData).then((res)=>{
                setteacherData({
                    "full_name": "",
                    "email": "",
                    "password": "",
                    "qualification": "",
                    "phone_no": "",
                    "skills": "",
                    "status": "success",
                });
            });
        }catch(err){
            console.log('error',err)
            setteacherData({'Status': 'error'})
        }
    };
    // End


    return(
        <div className="container mt-4">
            <div className="col-6 offset-3">
                <div className="card">
                    <h5 className="card-header">User Registration</h5>
                    <div className="card-body">
                        <form>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Name</label>
                                <input type="text" className="form-control"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Email</label>
                                <input type="email" className="form-control"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Username</label>
                                <input type="text" className="form-control"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputPassword1" className="form-label">Password</label>
                                <input type="password" className="form-control" id="exampleInputPassword1"/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Interests</label>
                                <textarea className="form-control"></textarea>
                                <div id="emailHelp" className="form-text">Ex: Python, Django, JavaScript</div>
                            </div>
                            <button type="submit" className="btn btn-primary">Register</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Registration;
